import{_ as a,o as t,c as r,ak as l}from"./chunks/framework.CDHwDzB5.js";const _=JSON.parse('{"title":"事件机制","description":"","frontmatter":{},"headers":[],"relativePath":"zh/event/event-overview.md","filePath":"zh/event/event-overview.md","lastUpdated":1771920474000}'),i={name:"zh/event/event-overview.md"};function n(o,e,s,h,c,d){return t(),r("div",null,[...e[0]||(e[0]=[l('<h1 id="事件机制" tabindex="-1">事件机制 <a class="header-anchor" href="#事件机制" aria-label="Permalink to “事件机制”">​</a></h1><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to “概述”">​</a></h2><p><strong>事件</strong> 是 TTHSD 内核在各种下载状态变化时向外部应用程序发送的结构化消息通知机制。这些事件采用 <strong>键值对</strong> 的形式，通过预定义的回调机制实时传递给调用方进行处理。</p><blockquote><p>Event 键值对的格式请去 <a href="/TTHSD/zh/event/event-format.html">Event 键值对 格式</a></p><p>Msg 键值对的格式请根据<strong>具体事件类型</strong>进行处理。</p></blockquote><h2 id="事件触发场景" tabindex="-1">事件触发场景 <a class="header-anchor" href="#事件触发场景" aria-label="Permalink to “事件触发场景”">​</a></h2><p>TTHSD内核会在以下关键场景下触发事件通知：</p><ul><li>下载任务初始化完成</li><li>单个文件开始下载</li><li>下载进度更新</li><li>单个文件下载完成</li><li>全部下载任务结束</li><li>异常错误发生</li><li>系统消息通知</li></ul><h2 id="事件传递方式" tabindex="-1">事件传递方式 <a class="header-anchor" href="#事件传递方式" aria-label="Permalink to “事件传递方式”">​</a></h2><p>事件消息可通过以下两种标准方式传递给调用程序：</p><h3 id="_1-本地回调函数-capi-方式" tabindex="-1">1. 本地回调函数（ CAPI 方式） <a class="header-anchor" href="#_1-本地回调函数-capi-方式" aria-label="Permalink to “1. 本地回调函数（ CAPI 方式）”">​</a></h3><p>通过 C 语言 API 直接注册的回调函数进行处理</p><h3 id="_2-远程回调url" tabindex="-1">2. 远程回调URL <a class="header-anchor" href="#_2-远程回调url" aria-label="Permalink to “2. 远程回调URL”">​</a></h3><p>通过网络协议进行消息传递：</p><ul><li><strong>Socket通信</strong>：基于TCP协议的可靠传输</li><li><strong>WebSocket通信</strong>：基于WebSocket协议的双向通信</li></ul><h2 id="注意事项" tabindex="-1">注意事项 <a class="header-anchor" href="#注意事项" aria-label="Permalink to “注意事项”">​</a></h2><ul><li>事件处理应当具备异常容错能力</li><li>事件分为<strong>两个</strong> 键值对 ： <ul><li><strong>event</strong>：包含事件类型、唯一标识符等基本信息</li><li><strong>msg</strong>：包含事件相关的详细数据，如下载进度、文件路径、错误信息等</li></ul></li></ul>',16)])])}const v=a(i,[["render",n]]);export{_ as __pageData,v as default};
